import React, { Component } from 'react'

class Form extends Component {
    state = {
        meuNome: '',
        minhaIdade: 0,
    }

    handleChange = (event) => {
        const { target } = event;
        const { meuNome, minhaIdade } = this.state;
        this.setState({
            meuNome: target.value,
        })
    }

  render() {
    return (
      <form>
        <input name="nome" value={this.state.meuNome} onChange={this.handleChange} type="text" />
        <input name="idade" value={this.state.minhaIdade} onChange={this.handleChange} type="number" />
        <textarea name="" id="" cols="30" rows="10"></textarea>
      </form>
    )
  }
}

export default Form
